import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import pizzaData from "./pizzaData";
function App() {
  return (
    <div className="container">
      <Header />
      <Menu />
      <Footer />
    </div>
  );
}

function Header() {
  const style = {};
  return (
    <header className="header">
      <h1 style={style}>Fast React Pizza Co.</h1>
    </header>
  );
}

function Menu() {
  const pizzas = pizzaData;
  const numPizzas = pizzas.length;
  return (
    <main className="menu">
      <h2>Our Menu</h2>

      {/* Always keep conditional rendering value boolean because a number or a string would be still rendered on the screen */}
      {numPizzas > 0 ? (
        <ul className="pizzas">
          {pizzaData.map((pizza, index) => (
            <Pizza pizzaObj={pizza} key={index} />
          ))}
        </ul>
      ) : (
        <p>We are still working on our menu. Please come back later :)</p>
      )}
    </main>
  );
}

function Pizza(props) {
  console.log(props);
  return (
    <li className={props.pizzaObj.soldOut ? "pizza sold-out" : "pizza"}>
      <img src={props.pizzaObj.photoName} alt={props.pizzaObj.name} />
      <div>
        <h3>{props.pizzaObj.name}</h3>
        <p>{props.pizzaObj.ingredients}</p>
        <span>${props.pizzaObj.price}</span>
      </div>
    </li>
  );
}

function Footer() {
  const hour = new Date().getHours();
  const openHour = 12;
  const closeHour = 23;
  const isOpen = hour >= openHour && hour < closeHour;
  console.log(isOpen);
  return (
    <footer className="footer">
      {isOpen ? (
        <order closing={closeHour} />
      ) : (
        <p>Sorry, we are closed now. We will open at {openHour}:00.</p>
      )}
    </footer>
  );
  // return React.createElement("footer", null, "We are currently open!");
}

function Order(props) {
  return (
    <div className="order">
      <p>We are open until {closing}:00. Come visit us or order online!</p>
      <button className="btn">Order</button>
    </div>
  );
}

// React v18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
